// Import required modules
const axios = require('axios');
const fs = require('fs');
const path = require('path');

// Function to download and save an image
async function downloadImage(url, destPath) {
	try {
		const response = await axios({
			method: 'GET',
			url: url,
			responseType: 'stream'
		});

		response.data.pipe(fs.createWriteStream(destPath));

		return new Promise((resolve, reject) => {
			response.data.on('end', () => {
				resolve();
			});

			response.data.on('error', (err) => {
				reject(err);
			});
		});
	} catch (err) {
		throw new Error(`Failed to download image: ${err.message}`);
	}
}

function arrLast(arr) { return arr.length > 0 ? arr[arr.length - 1] : null; }
function stringAfterLast(sFull, sSub) {
  let parts = sFull.split(sSub);
  return arrLast(parts);
}

async function downloadThumbnails(objectsList,isNews=false) {
	for (const obj of objectsList) {
		const url = isNews?obj.thumbnail:`https://llmrecs.z13.web.core.windows.net/${obj.thumbnail}`; //obj.thumbnail;
		const savePath = path.join(__dirname,'..','y',`${isNews?stringAfterLast(obj.thumbnail,'/'):obj.thumbnail}`);

		try {
			await downloadImage(url, savePath);
			console.log(`Downloaded thumbnail for ${obj.ID} to ${savePath}`);
		} catch (err) {
			console.error(`Error downloading thumbnail for ${obj.name}:`, err);
		}

		//return;
	}
}

